""" this program takes an mRNA string and returns the protein sequence

the plan is to read the string in, parse it into 3 letter 'words' then 

run those words through a dictionary that has the RNA code as the key's 

and the one letter amino acid codes as the value. append that to the string 

and bob is your uncle"""
import math

def word_slice(sequence, word_len = 3):
    mRNA_codon = []
    for i in range(int(math.ceil(len(sequence)/word_len))):
        word = sequence[i * word_len: (i + 1) * word_len]
        mRNA_codon.append(word)
    return mRNA_codon
    
def codon_to_protein(codon_list):
    RNAdict = {"UUU":"F", "UUC":"F", "UUA":"L", "UUG":"L",
    "UCU":"S", "UCC":"S", "UCA":"S", "UCG":"S",
    "UAU":"Y", "UAC":"Y", "UAA":" ", "UAG":" ",
    "UGU":"C", "UGC":"C", "UGA":" ", "UGG":"W",
    "CUU":"L", "CUC":"L", "CUA":"L", "CUG":"L",
    "CCU":"P", "CCC":"P", "CCA":"P", "CCG":"P",
    "CAU":"H", "CAC":"H", "CAA":"Q", "CAG":"Q",
    "CGU":"R", "CGC":"R", "CGA":"R", "CGG":"R",
    "AUU":"I", "AUC":"I", "AUA":"I", "AUG":"M",
    "ACU":"T", "ACC":"T", "ACA":"T", "ACG":"T",
    "AAU":"N", "AAC":"N", "AAA":"K", "AAG":"K",
    "AGU":"S", "AGC":"S", "AGA":"R", "AGG":"R",
    "GUU":"V", "GUC":"V", "GUA":"V", "GUG":"V",
    "GCU":"A", "GCC":"A", "GCA":"A", "GCG":"A",
    "GAU":"D", "GAC":"D", "GAA":"E", "GAG":"E",
    "GGU":"G", "GGC":"G", "GGA":"G", "GGG":"G",}
    aa_sequence = ""
    for codon in codon_list:
        amino_acid = RNAdict[codon]
        aa_sequence += amino_acid
    return aa_sequence
    
Codon_list = word_slice("")
print(codon_to_protein(Codon_list))
        